import React from 'react'

import { twMerge } from 'tailwind-merge'

/* eslint-disable no-unused-vars */
export enum IconName {
  THUMBS_UP = 'data:image/png;base64,',

  THUMBS_DOWN = 'data:image/png;base64,'
}

interface IIconProps extends React.HTMLAttributes<HTMLElement> {
  iconName: IconName
}

export const Icon = ({ iconName, ...props }: IIconProps) => {
  return (
    <div
      {...props}
      className={twMerge(
        `rounded-full fle shadow-shadow-black ${props.className}`,
        iconName === IconName.THUMBS_DOWN ? 'bg-invalid' : 'bg-valid'
      )}
    >
      <img className="" src={iconName} />
    </div>
  )
}
